void parseCommand ( Player jugador , String verb , String arguments )
{
	if ( verb.equalsIgnoreCase( "saltar" ) || verb.equalsIgnoreCase( "entrar" ) )
	{
		jugador.forceCommand("ir hacia abajo");
		end();
	}
}

