  			//en vez de molestarnos en que las inscripciones sean un objeto, tratamos el único comando coherente que se puede referir a ellas a pelo.
  			void parseCommand ( Player jugador , String verb , String arguments )
			{
				if ( verb.equalsIgnoreCase( "leer" ) )
				{
					if ( arguments.indexOf("inscripción")>=0 || arguments.indexOf("inscripciones")>=0 || arguments.indexOf("pared")>=0 || arguments.indexOf("paredes")>=0 )
					{
						//sustituimos el comando por mirar.
						jugador.forceCommand("mirar inscripciones");
						end();
					}
				}
			}	
			String getDescription ( Entity viewer )
			{
			
				System.out.println("JA");
			
				boolean n = self.getPath ( true , Path.NORTE ).isOpen();
				boolean s = self.getPath ( true , Path.SUR ).isOpen();
				boolean e = self.getPath ( true , Path.ESTE ).isOpen();
				boolean w = self.getPath ( true , Path.OESTE ).isOpen();
				boolean abj = self.getPath ( true , Path.ABAJO ).isOpen();
				
				System.out.println("JO");
				
				int openExits=0;
				if ( n ) openExits++; if ( s ) openExits++; if ( e ) openExits++; if ( w ) openExits++; if ( abj ) openExits++;
				int remainingExits = openExits;
				
				System.out.println("JU");
				
				String exitDes = "";
				
				System.out.println("JI");
				
				if ( openExits == 0 )
				{
					exitDes = "No hay salida en ninguna dirección";
				}
				else
				{
					exitDes = "De aquí puedes ir hacia ";
					while ( remainingExits > 0 )
					{
						if ( openExits > 1 && remainingExits == 1 )
						{
							exitDes += " y ";
						}
						else if ( openExits > 1 && remainingExits < openExits )
						{
							exitDes += ", ";
						}
						if ( n ) { exitDes+="el norte"; n = false; }
						else if ( s ) { exitDes+="el sur"; s = false; }
						else if ( e ) { exitDes+="el este"; e = false; }
						else if ( w ) { exitDes+="el oeste"; w = false; }
						else if ( abj ) { exitDes+="abajo"; w = false; }
						remainingExits--;
					}
					//exitDes += ".";
				}
				
				System.out.println("JE");
				
				return (
						"Te encuentras en una de las monótonas habitaciones cuadradas del Laberinto de Zendyr. "
						+ "En el suelo se pueden ver los números " + "(" + self.getPropertyValueAsInteger("row") + "," + self.getPropertyValueAsInteger("col") + "). " + self.getDescription(viewer)
						+ "\n" + exitDes +"." 
					)
				;
				
				//return ("Aaah, la déscguipsión... "  + self.getID() + " " + self.getDescription(viewer) + "(" + self.getPropertyValueAsInteger("row") + "," + self.getPropertyValueAsInteger("col") + ")" + exitDes );
			}

Item.Code:
void parseCommand ( Mobile m , String verb , String args )
{
	if ( verb.equalsIgnoreCase("pulsar") || verb.equalsIgnoreCase("presionar") )
	{
		ReturnValue retval = new ReturnValue(null);
		List miNivel = self.getRelatedEntitiesByValue("mylevel",true);
		if ( miNivel.size() > 0 )
		{
			ejecutado = world.execCode( "activateButtonEffect" , new Object[] { miNivel.get(0) , self.getPropertyValueAsString("effectstring") } , retval );
			m.escribir("Se oye un fuerte sonido chirriante.\n");
			end();
		}
	}
}

